function performDBOperation(storeName, mode, operation) {
    return new Promise((resolve, reject) => {
        if (!db) {
            console.error(
                `performDBOperation: –ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö (db) –Ω–µ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–∞! Store: ${storeName}, Mode: ${mode}`,
            );
            return reject(new Error('–ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö –Ω–µ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–∞'));
        }
        try {
            if (!db.objectStoreNames.contains(storeName)) {
                const errorMsg = `–•—Ä–∞–Ω–∏–ª–∏—â–µ –æ–±—ä–µ–∫—Ç–æ–≤ '${storeName}' –Ω–µ –Ω–∞–π–¥–µ–Ω–æ –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö. –î–æ—Å—Ç—É–ø–Ω—ã–µ: ${Array.from(
                    db.objectStoreNames,
                ).join(', ')}`;
                console.error(`performDBOperation: ${errorMsg}`);
                return reject(new Error(errorMsg));
            }

            const transaction = db.transaction(storeName, mode);

            transaction.oncomplete = () => {};
            transaction.onerror = (e) => {
                const error = e.target.error;
                const errorDetails = error
                    ? `${error.name}: ${error.message}`
                    : '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏';
                console.error(
                    `performDBOperation: –û—à–∏–±–∫–∞ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏ –¥–ª—è '${storeName}' (mode: ${mode}). –î–µ—Ç–∞–ª–∏: ${errorDetails}`,
                    error,
                );

                reject(error || new Error(`–û—à–∏–±–∫–∞ —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–∏ –¥–ª—è ${storeName}: ${errorDetails}`));
            };
            transaction.onabort = (e) => {
                const error = e.target.error;
                const errorDetails = error
                    ? `${error.name}: ${error.message}`
                    : '–¢—Ä–∞–Ω–∑–∞–∫—Ü–∏—è –ø—Ä–µ—Ä–≤–∞–Ω–∞ –±–µ–∑ —è–≤–Ω–æ–π –æ—à–∏–±–∫–∏';
                console.warn(
                    `performDBOperation: –¢—Ä–∞–Ω–∑–∞–∫—Ü–∏—è –¥–ª—è '${storeName}' (mode: ${mode}) –ü–†–ï–†–í–ê–ù–ê. –î–µ—Ç–∞–ª–∏: ${errorDetails}`,
                    error,
                );
                reject(error || new Error(`–¢—Ä–∞–Ω–∑–∞–∫—Ü–∏—è –¥–ª—è ${storeName} –ø—Ä–µ—Ä–≤–∞–Ω–∞: ${errorDetails}`));
            };

            const store = transaction.objectStore(storeName);
            const request = operation(store);

            if (!(request instanceof IDBRequest)) {
                console.error(
                    `performDBOperation: –ö–æ–ª–±—ç–∫ 'operation' –Ω–µ –≤–µ—Ä–Ω—É–ª IDBRequest –¥–ª—è ${storeName}. –í–µ—Ä–Ω—É–ª:`,
                    request,
                );
                return reject(
                    new Error(`–í–Ω—É—Ç—Ä–µ–Ω–Ω—è—è –æ—à–∏–±–∫–∞: –æ–ø–µ—Ä–∞—Ü–∏—è –¥–ª—è ${storeName} –Ω–µ –≤–µ—Ä–Ω—É–ª–∞ –∑–∞–ø—Ä–æ—Å.`),
                );
            }

            request.onsuccess = (e) => {
                resolve(e.target.result);
            };
            request.onerror = (e) => {
                const error = e.target.error;
                const errorDetails = error
                    ? `${error.name}: ${error.message}`
                    : '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞';
                console.error(
                    `performDBOperation: –û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ –∫ —Ö—Ä–∞–Ω–∏–ª–∏—â—É '${storeName}' (mode: ${mode}). –î–µ—Ç–∞–ª–∏: ${errorDetails}`,
                    error,
                );
                reject(error || new Error(`–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ –∫ ${storeName}: ${errorDetails}`));
            };
        } catch (error) {
            console.error(
                `performDBOperation: –ò—Å–∫–ª—é—á–µ–Ω–∏–µ –ø—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ –≤—ã–ø–æ–ª–Ω–∏—Ç—å –æ–ø–µ—Ä–∞—Ü–∏—é –¥–ª—è '${storeName}' (mode: ${mode}).`,
                error,
            );
            reject(error);
        }
    });
}

function saveToIndexedDB(storeName, data, key = null) {
    return performDBOperation(storeName, 'readwrite', (store) =>
        store.put(data, ...(key !== null ? [key] : [])),
    );
}

function getFromIndexedDB(storeName, key) {
    const storeConfig = storeConfigs.find((sc) => sc.name === storeName);
    let keyToUse = key;

    if (storeConfig && storeConfig.options && storeConfig.options.autoIncrement) {
        if (typeof key === 'string') {
            const parsedKey = parseInt(key, 10);

            if (!isNaN(parsedKey) && String(parsedKey) === key) {
                keyToUse = parsedKey;
            }
        }
    }
    return performDBOperation(storeName, 'readonly', (store) => store.get(keyToUse));
}

