function base64ToBlob(base64, mimeType = '') {
    if (!base64 || typeof base64 !== 'string') {
        console.error(`–û—à–∏–±–∫–∞ –∫–æ–Ω–≤–µ—Ä—Ç–∞—Ü–∏–∏ Base64 –≤ Blob: –ü–µ—Ä–µ–¥–∞–Ω–∞ –Ω–µ–≤–∞–ª–∏–¥–Ω–∞—è —Å—Ç—Ä–æ–∫–∞ Base64.`);
        return null;
    }
    try {
        const base64Data = base64.includes(',') ? base64.split(',')[1] : base64;
        if (!base64Data) {
            console.error(
                `–û—à–∏–±–∫–∞ –∫–æ–Ω–≤–µ—Ä—Ç–∞—Ü–∏–∏ Base64 –≤ Blob: –°—Ç—Ä–æ–∫–∞ Base64 –ø—É—Å—Ç–∞ –ø–æ—Å–ª–µ —É–¥–∞–ª–µ–Ω–∏—è –ø—Ä–µ—Ñ–∏–∫—Å–∞.`,
            );
            return null;
        }
        const byteCharacters = atob(base64Data);
        const byteNumbers = new Array(byteCharacters.length);
        for (let i = 0; i < byteCharacters.length; i++) {
            byteNumbers[i] = byteCharacters.charCodeAt(i);
        }
        const byteArray = new Uint8Array(byteNumbers);
        return new Blob([byteArray], { type: mimeType });
    } catch (error) {
        console.error(
            `–û—à–∏–±–∫–∞ –∫–æ–Ω–≤–µ—Ä—Ç–∞—Ü–∏–∏ Base64 –≤ Blob (MIME: ${mimeType}, Base64 –Ω–∞—á–∞–ª–æ: ${base64.substring(
                0,
                30,
            )}...):`,
            error,
        );
        if (error instanceof DOMException && error.name === 'InvalidCharacterError') {
            console.error('   > –í–µ—Ä–æ—è—Ç–Ω–æ, —Å—Ç—Ä–æ–∫–∞ Base64 —Å–æ–¥–µ—Ä–∂–∏—Ç –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–µ —Å–∏–º–≤–æ–ª—ã.');
        }
        return null;
    }
}

