async function loadMainAlgoCollapseState() {
    try {
        const raw = localStorage.getItem(MAIN_ALGO_COLLAPSE_KEY);
        if (!raw) return { stepsCount: 0, collapsedIndices: [] };
        const parsed = JSON.parse(raw);
        const stepsCount =
            parsed && typeof parsed.stepsCount === 'number' && parsed.stepsCount >= 0
                ? parsed.stepsCount
                : 0;
        const collapsedIndices = Array.isArray(parsed?.collapsedIndices)
            ? parsed.collapsedIndices.filter((i) => Number.isInteger(i) && i >= 0)
            : [];
        return { stepsCount, collapsedIndices };
    } catch (e) {
        console.warn('[MainAlgoCollapseState] Invalid JSON in localStorage:', e);
        return { stepsCount: 0, collapsedIndices: [] };
    }
}

async function saveMainAlgoCollapseState(state) {
    try {
        const stepsCount =
            state && typeof state.stepsCount === 'number' && state.stepsCount >= 0
                ? state.stepsCount
                : 0;
        const uniqueValid = Array.isArray(state?.collapsedIndices)
            ? Array.from(
                  new Set(state.collapsedIndices.filter((i) => Number.isInteger(i) && i >= 0)),
              )
            : [];
        const payload = { stepsCount, collapsedIndices: uniqueValid };
        localStorage.setItem(MAIN_ALGO_COLLAPSE_KEY, JSON.stringify(payload));
        return true;
    } catch (e) {
        console.error('[MainAlgoCollapseState] Save failed:', e);
        return false;
    }
}

async function getAllFromIndex(storeName, indexName, indexValue) {
    const debugOn = typeof window !== 'undefined' && !!window.DB_LOG;
    const dlog = (...args) => {
        if (debugOn) console.debug(...args);
    };

    if (!db) {
        const msg = `getAllFromIndex: –ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö –Ω–µ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–∞. store=${storeName}, index=${indexName}`;
        console.error(msg);
        return Promise.reject(new Error(msg));
    }
    if (!storeName || !indexName || typeof indexValue === 'undefined') {
        const msg = `getAllFromIndex: –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–µ –∞—Ä–≥—É–º–µ–Ω—Ç—ã: storeName=${storeName}, indexName=${indexName}, indexValue=${indexValue}`;
        console.error(msg);
        return Promise.reject(new Error(msg));
    }

    return new Promise((resolve, reject) => {
        try {
            if (!db.objectStoreNames || !db.objectStoreNames.contains(storeName)) {
                const availableStores = Array.from(db.objectStoreNames || []).join(', ');
                const msg = `getAllFromIndex: –°—Ç–æ—Ä '${storeName}' –Ω–µ –Ω–∞–π–¥–µ–Ω. –î–æ—Å—Ç—É–ø–Ω—ã–µ: ${availableStores}`;
                console.error(msg);
                return reject(new Error(msg));
            }

            const tx = db.transaction(storeName, 'readonly');
            const store = tx.objectStore(storeName);

            if (!store.indexNames || !store.indexNames.contains(indexName)) {
                const availableIdx = Array.from(store.indexNames || []).join(', ');
                const msg = `getAllFromIndex: –ò–Ω–¥–µ–∫—Å '${indexName}' –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ —Å—Ç–æ—Ä–µ '${storeName}'. –î–æ—Å—Ç—É–ø–Ω—ã–µ –∏–Ω–¥–µ–∫—Å—ã: ${availableIdx}`;
                console.error(msg);
                return reject(new Error(msg));
            }

            const index = store.index(indexName);
            dlog(
                `getAllFromIndex ‚Üí ${storeName}/${indexName}, value=`,
                indexValue,
                `(type: ${typeof indexValue})`,
            );

            const request = index.getAll(indexValue);

            request.onsuccess = (e) => {
                const result = e.target?.result || [];
                dlog(
                    `getAllFromIndex ‚Üê OK ${storeName}/${indexName}=${String(indexValue)} ‚Üí ${
                        result.length
                    } rows`,
                );
                resolve(result);
            };

            request.onerror = (e) => {
                const err =
                    e.target?.error ||
                    new Error(`getAllFromIndex: –æ—à–∏–±–∫–∞ —á—Ç–µ–Ω–∏—è ${storeName}/${indexName}`);
                console.error(
                    `getAllFromIndex: –æ—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞ –ø–æ ${storeName}/${indexName}=${String(
                        indexValue,
                    )}:`,
                    err,
                );
                reject(err);
            };

            tx.onerror = (e) =>
                dlog(`getAllFromIndex: tx.onerror ${storeName}/${indexName}`, e.target?.error);
            tx.onabort = (e) =>
                dlog(`getAllFromIndex: tx.onabort ${storeName}/${indexName}`, e.target?.error);
        } catch (error) {
            console.error(
                `getAllFromIndex: –∏—Å–∫–ª—é—á–µ–Ω–∏–µ –ø—Ä–∏ –¥–æ—Å—Ç—É–ø–µ –∫ ${storeName}/${indexName}:`,
                error,
            );
            reject(error);
        }
    });
}

