async function processImageFile(fileOrBlob) {
    return new Promise((resolve, reject) => {
        if (!(fileOrBlob instanceof Blob)) {
            return reject(new Error('–ü—Ä–µ–¥–æ—Å—Ç–∞–≤–ª–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –Ω–µ —è–≤–ª—è—é—Ç—Å—è —Ñ–∞–π–ª–æ–º –∏–ª–∏ Blob.'));
        }
        const img = new Image();
        const reader = new FileReader();
        reader.onload = (e_reader) => {
            if (!e_reader.target || typeof e_reader.target.result !== 'string') {
                return reject(new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–æ—á–∏—Ç–∞—Ç—å –¥–∞–Ω–Ω—ã–µ —Ñ–∞–π–ª–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è.'));
            }
            img.onload = () => {
                const canvas = document.createElement('canvas');
                const MAX_WIDTH = 1280,
                    MAX_HEIGHT = 1024;
                let width = img.naturalWidth || img.width,
                    height = img.naturalHeight || img.height;
                if (width === 0 || height === 0) {
                    return reject(new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å —Ä–∞–∑–º–µ—Ä—ã –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è.'));
                }
                if (width > MAX_WIDTH) {
                    height *= MAX_WIDTH / width;
                    width = MAX_WIDTH;
                }
                if (height > MAX_HEIGHT) {
                    width *= MAX_HEIGHT / height;
                    height = MAX_HEIGHT;
                }
                canvas.width = Math.round(width);
                canvas.height = Math.round(height);
                const ctx = canvas.getContext('2d');
                if (!ctx) {
                    return reject(new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å 2D –∫–æ–Ω—Ç–µ–∫—Å—Ç Canvas.'));
                }
                try {
                    ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
                } catch (drawError) {
                    console.error('–û—à–∏–±–∫–∞ –æ—Ç—Ä–∏—Å–æ–≤–∫–∏ –Ω–∞ Canvas:', drawError);
                    return reject(new Error('–û—à–∏–±–∫–∞ –æ—Ç—Ä–∏—Å–æ–≤–∫–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è.'));
                }
                canvas.toBlob(
                    (blob) => {
                        if (blob) {
                            console.log(
                                `–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ–±—Ä–∞–±–æ—Ç–∞–Ω–æ –∏ —Å–∂–∞—Ç–æ –≤ WebP. –†–∞–∑–º–µ—Ä: ${(
                                    blob.size / 1024
                                ).toFixed(1)} KB`,
                            );
                            resolve(blob);
                        } else {
                            canvas.toBlob(
                                (jpegBlob) => {
                                    if (jpegBlob) {
                                        console.log(
                                            `–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ–±—Ä–∞–±–æ—Ç–∞–Ω–æ –∏ —Å–∂–∞—Ç–æ –≤ JPEG. –†–∞–∑–º–µ—Ä: ${(
                                                jpegBlob.size / 1024
                                            ).toFixed(1)} KB`,
                                        );
                                        resolve(jpegBlob);
                                    } else {
                                        reject(
                                            new Error(
                                                '–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å Blob –∏–∑ Canvas (–Ω–∏ WebP, –Ω–∏ JPEG)',
                                            ),
                                        );
                                    }
                                },
                                'image/jpeg',
                                0.85,
                            );
                        }
                    },
                    'image/webp',
                    0.8,
                );
            };
            img.onerror = (err) =>
                reject(new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –≤ Image –æ–±—ä–µ–∫—Ç.'));
            img.src = e_reader.target.result;
        };
        reader.onerror = (err) => reject(new Error('–ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–æ—á–∏—Ç–∞—Ç—å —Ñ–∞–π–ª –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è.'));
        reader.readAsDataURL(fileOrBlob);
    });
}

