    loadingIndicator.innerHTML = '<i class="fas fa-spinner fa-spin"></i> –ó–∞–≥—Ä—É–∑–∫–∞...';
    loadingIndicator.classList.remove('hidden');
    lightboxImage.classList.add('hidden');
