async function exportBlacklistToExcel() {
    try {
        if (typeof XLSX === 'undefined') {
            showNotification(
                '–ë–∏–±–ª–∏–æ—Ç–µ–∫–∞ XLSX –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω–∞. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –≤ index.html.',
                'error',
            );
            return;
        }
        const entries = (await getAllBlacklistEntriesDB()) || [];
        if (!entries.length) {
            showNotification('–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞.', 'warning');
            return;
        }

        const levelLabel = (n) => (n === 3 ? '–í—ã—Å–æ–∫–∏–π' : n === 2 ? '–°—Ä–µ–¥–Ω–∏–π' : '–ù–∏–∑–∫–∏–π');
        const sanitizeExcelText = (val) => {
            if (val === null || val === undefined) return '';
            const s = String(val);
            return /^[=+\-@]/.test(s) ? "'" + s : s;
        };
        const fmtDate = (iso) => {
            if (!iso) return '';
            const d = new Date(iso);
            if (Number.isNaN(d.getTime())) return '';
            const pad = (x) => String(x).padStart(2, '0');
            return `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())} ${pad(
                d.getHours(),
            )}-${pad(d.getMinutes())}`;
        };

        const grouped = { 1: [], 2: [], 3: [] };
        for (const e of entries) {
            const lvl = [1, 2, 3].includes(e.level) ? e.level : 1;
            grouped[lvl].push({
                –û—Ä–≥–∞–Ω–∏–∑–∞—Ü–∏—è: sanitizeExcelText(e.organizationName || ''),
                –ò–ù–ù: sanitizeExcelText(e.inn || ''),
                –¢–µ–ª–µ—Ñ–æ–Ω: sanitizeExcelText(e.phone || ''),
                –£—Ä–æ–≤–µ–Ω—å: levelLabel(lvl),
                '–î–∞—Ç–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è': fmtDate(e.dateAdded || e.dateUpdated),
                –ü—Ä–∏–º–µ—á–∞–Ω–∏–µ: sanitizeExcelText(e.notes || ''),
            });
        }

        const headers = [
            '–û—Ä–≥–∞–Ω–∏–∑–∞—Ü–∏—è',
            '–ò–ù–ù',
            '–¢–µ–ª–µ—Ñ–æ–Ω',
            '–£—Ä–æ–≤–µ–Ω—å',
            '–î–∞—Ç–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è',
            '–ü—Ä–∏–º–µ—á–∞–Ω–∏–µ',
        ];
        const wb = XLSX.utils.book_new();
        const addSheet = (rows, name) => {
            const ws = XLSX.utils.json_to_sheet(rows, { header: headers });
            const colWidths = headers.map((h) => {
                const maxLen = Math.max(
                    h.length,
                    ...rows.map((r) => (r[h] ? String(r[h]).length : 0)),
                );
                return { wch: Math.min(Math.max(maxLen + 2, 10), 60) };
            });
            ws['!cols'] = colWidths;
            XLSX.utils.book_append_sheet(wb, ws, name);
        };

        addSheet(grouped[1], '–ù–∏–∑–∫–∏–π (—É—Ä–æ–≤–µ–Ω—å 1)');
        addSheet(grouped[2], '–°—Ä–µ–¥–Ω–∏–π (—É—Ä–æ–≤–µ–Ω—å 2)');
        addSheet(grouped[3], '–í—ã—Å–æ–∫–∏–π (—É—Ä–æ–≤–µ–Ω—å 3');

        const ts = new Date().toISOString().slice(0, 19).replace('T', '_').replace(/:/g, '-');
        XLSX.writeFile(wb, `–ß–µ—Ä–Ω—ã–π_—Å–ø–∏—Å–æ–∫_–∂–∞–±_${ts}.xlsx`);
        showNotification('–≠–∫—Å–ø–æ—Ä—Ç –≤ Excel –≤—ã–ø–æ–ª–Ω–µ–Ω.', 'success');
    } catch (err) {
        console.error('[exportBlacklistToExcel] –û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞:', err);
        showNotification(
            `–û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞ –≤ Excel: ${err?.message || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞'}`,
            'error',
        );
    }
}

