async function addPdfRecords(files, parentType, parentId) {
    if (!db) {
        console.error('[addPdfRecords] DB is not ready');
        showNotification && showNotification('–ë–î –Ω–µ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–∞', 'error');
        return [];
    }
    const pType = String(parentType || '').trim();
    const pId = String(parentId ?? '').trim();
    if (!pType || !pId) {
        console.error('[addPdfRecords] Invalid parent binding:', { parentType, parentId });
        showNotification &&
            showNotification('–ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–∏–∫—Ä–µ–ø–∏—Ç—å PDF: –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞—è –ø—Ä–∏–≤—è–∑–∫–∞', 'error');
        return [];
    }
    const parentKey = makeParentKey(pType, pId);

    const results = [];
    let existing = [];
    try {
        existing = await getPdfsForParent(pType, pId);
    } catch {}
    const existingKeys = new Set(
        (existing || []).filter(Boolean).map((r) => `${(r && r.name) || ''}|${(r && r.size) || 0}`),
    );

    const uniq = [];
    for (const f of Array.from(files || [])) {
        if (!f) continue;
        const k = `${(f && f.name) || ''}|${(f && f.size) || 0}`;
        if (!existingKeys.has(k)) {
            existingKeys.add(k);
            uniq.push(f);
        }
    }

    for (const file of uniq) {
        if (!isPdfFile(file)) {
            console.warn('[addPdfRecords] skipped non-pdf', file && file.name);
            continue;
        }
        const rec = {
            parentType: pType,
            parentId: pId,
            parentKey,
            name: file.name,
            size: file.size || 0,
            mime: 'application/pdf',
            blob: file,
            uploadedAt: new Date().toISOString(),
        };
        try {
            results.push(await saveToIndexedDB('pdfFiles', rec));
        } catch (e) {
            console.error('[addPdfRecords] failed', e);
        }
    }
    return results;
}

async function getPdfsForParent(parentType, parentId) {
    try {
        if (!db) throw new Error('DB not ready');
        const pType = String(parentType || '').trim();
        const pId = String(parentId ?? '').trim();
        if (!pType || !pId) {
            return [];
        }
        const tx = db.transaction('pdfFiles', 'readonly');
        const store = tx.objectStore('pdfFiles');
        if (store.indexNames && store.indexNames.contains('parentKey')) {
            const byKey = await getAllFromIndex(
                'pdfFiles',
                'parentKey',
                makeParentKey(parentType, parentId),
            );
            if (Array.isArray(byKey) && byKey.length) return byKey;
        }
        const all = (await getAllFromIndexedDB('pdfFiles')) || [];
        const key = makeParentKey(parentType, parentId);
        const withKey = all.filter((r) => r && String(r.parentKey || '') === key);
        if (withKey.length) return withKey;
        return all.filter(
            (r) => r && String(r.parentId) === String(parentId) && r.parentType === parentType,
        );
    } catch (e) {
        console.error('[getPdfsForParent] failed', e);
        return [];
    }
}

